import React, { Component } from "react";
import "./App.css";


// 1. CREAR UN TABLA DE PRESUPUESTO ANUAL (BUDGET + 12 MESES CON INCOME, EXPENSES, RESULT)
// 2. CAMPOS MODIFICABLES
// 3. CAMPOS CALCULADOS AUTOMATICAMENTE

// 4. SE GUARDE EN UNA BASE DE DATOS LOS CAMBIOS MONGODB
// 5. SE MUESTRE EL ARRAY GRABADO DE LA BASE DE DATOS

// DESEABLES
// PLATILLA POR USUARIO
// PLANTILLA POR AÃ‘O
// PLANTILLA EN LA QUE SE PUEDAN AGREGAR MAS FILAS PARA CALCULOS DETALLADOS



const arrayBudget = {
  budget: [1000, 800],
  jan: [1000, 800],
  feb: [900, 800],
  mar: [1000, 800],
  apr: [850, 800],
  may: [1000, 800],
  jun: [1000, 1000],
  jul: [1000, 890],
  ago: [600, 800],
  sep: [1000, 950],
  oct: [1200, 800],
  nov: [1000, 750],
  dec: [2000, 800]
};


function Averageincome() {

  let x = 0;
  let y = 0;
  x = ((arrayBudget.jan[0] + arrayBudget.feb[0] + arrayBudget.mar[0]
    + arrayBudget.apr[0] + arrayBudget.may[0] + arrayBudget.jun[0]
    + arrayBudget.jul[0] + arrayBudget.ago[0] + arrayBudget.sep[0]
    + arrayBudget.oct[0] + arrayBudget.nov[0] + arrayBudget.dec[0]
  ) / 12);
  y = x.toFixed(2);
  return y;
}

function Averageexpense() {

  let x = 0;
  let y = 0;
  x = ((arrayBudget.jan[1] + arrayBudget.feb[1] + arrayBudget.mar[1]
    + arrayBudget.apr[1] + arrayBudget.may[1] + arrayBudget.jun[1]
    + arrayBudget.jul[1] + arrayBudget.ago[1] + arrayBudget.sep[1]
    + arrayBudget.oct[1] + arrayBudget.nov[1] + arrayBudget.dec[1]
  ) / 12);
  y = x.toFixed(2);
  return y;
}


// 

class Headertable extends Component {
  render() {
    return (
      <thead>
        <tr>
          <th scope="col"><input id="buttonSubmit" class="btn btn-primary" type="submit" value="Submit" /></th>
          <th scope="col" class="resPath">BUDGET</th>
          <th scope="col">JAN</th>
          <th scope="col">FEB</th>
          <th scope="col">MAR</th>
          <th scope="col">APR</th>
          <th scope="col">MAY</th>
          <th scope="col">JUN</th>
          <th scope="col">JUL</th>
          <th scope="col">AGO</th>
          <th scope="col">SEP</th>
          <th scope="col">OCT</th>
          <th scope="col">NOV</th>
          <th scope="col">DEC</th>
          <th scope="col" class="resPath" >AVERAGE</th>
          <th scope="col" class="resPath" >TOTAL</th>
        </tr>
      </thead>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      iBudget: 0,
      iJan:0

    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;

    const value = target.type === 'checkbox' ? target.checked : target.value;

    const name = target.name;


    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <div className="container">

        <div className="alert alert-primary">
          <h2>PERSONAL FINANCE</h2>
        </div>
        <div>
          <form onSubmit={this.handleSubmit}>
            <table className="table table-sm">
              <Headertable />
              <tbody>
                <tr>
                  <th scope="row" class="resPath" >INCOME</th>
                  <td class="resPath"><input name="iBudget" type="number" className="inputSquare" value={this.state.iBudget} onChange={this.handleChange} /></td>
                  <td><input name="iBudget" type="number" className="inputSquare" value={this.state.janBudget} onChange={this.handleChange} /></td>
                  <td>{arrayBudget.feb[0]}</td>
                  <td>{arrayBudget.mar[0]}</td>
                  <td>{arrayBudget.apr[0]}</td>
                  <td>{arrayBudget.may[0]}</td>
                  <td>{arrayBudget.jun[0]}</td>
                  <td>{arrayBudget.jul[0]}</td>
                  <td>{arrayBudget.ago[0]}</td>
                  <td>{arrayBudget.sep[0]}</td>
                  <td>{arrayBudget.oct[0]}</td>
