import React, { Component } from "react";
import "./App.css";



// 1. CREAR UN TABLA DE PRESUPUESTO ANUAL (BUDGET + 12 MESES CON INCOME, EXPENSES, RESULT)
// 2. CAMPOS MODIFICABLES
// 3. CAMPOS CALCULADOS AUTOMATICAMENTE

// 4. SE GUARDE EN UNA BASE DE DATOS LOS CAMBIOS MONGODB
// 5. SE MUESTRE EL ARRAY GRABADO DE LA BASE DE DATOS

// DESEABLES
// PLATILLA POR USUARIO
// PLANTILLA POR AÃ‘O
// PLANTILLA EN LA QUE SE PUEDAN AGREGAR MAS FILAS PARA CALCULOS DETALLADOS



const arrayBudge02 = [
  {
    month: "budget",
    income: "2100",
    expense: "800"
  },
  {
    month: "jan",
    income: "1100",
    expense: "800"
  },
  {
    month: "feb",
    income: "1100",
    expense: "800"
  }, {
    month: "mar",
    income: "1100",
    expense: "800"
  },
  {
    month: "apr",
    income: "1100",
    expense: "800"
  },
  {
    month: "may",
    income: "1100",
    expense: "800"
  },
  {
    month: "jun",
    income: "1100",
    expense: "800"
  },
  {
    month: "jul",
    income: "1100",
    expense: "800"
  },
  {
    month: "ago",
    income: "1100",
    expense: "800"
  },
  {
    month: "sep",
    income: "1100",
    expense: "800"
  },
  {
    month: "oct",
    income: "1100",
    expense: "800"
  },
  {
    month: "nov",
    income: "1100",
    expense: "800"
  },
  {
    month: "dec",
    income: "1100",
    expense: "800"
  }
];



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mainarray: arrayBudge02

    };

    this.handleInputChange = this.handleInputChange.bind(this);

  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }


  render() {

    // CALCULOS

    const xincome = 0;
    let x1, x2= 0;
    let xexpense = 0;
    let i;
    for (i = 1; i < this.state.mainarray.length; i++) {
      x1 += parseFloat(this.state.mainarray[i].income);
      xincome = x1/12;
      x2 += parseFloat(this.state.mainarray[i].expense);
      xexpense = x2/12;
    }



    return (
      <div>

        <br />
        <div className="container">
          <div className="alert alert-primary">
            <h2>PERSONAL FINANCE</h2>
          </div>
          <div>
            <form onSubmit={this.handleSubmit}>
              <table className="table table-sm">
                <tr>
                  <th scope="col"><button type="button" class="btn btn-success" onClick={this.handleFormSubmit}>SUBMIT</button></th>
                  <th scope="col" className="resPath">{this.state.mainarray[0].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[1].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[2].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[3].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[4].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[5].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[6].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[7].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[8].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[9].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[10].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[11].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[12].month.toUpperCase()}</th>
                </tr >

                <tbody>
                  <tr>
                    <th scope="row" class="resPath" >INCOME</th>
                    <td class="resPath"><input name="iBudget" type="number" className="inputSquare" value={this.state.mainarray[0].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iJan" type="number" className="inputSquare" value={this.state.mainarray[1].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iFeb" type="number" className="inputSquare" value={this.state.mainarray[2].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iMar" type="number" className="inputSquare" value={this.state.mainarray[3].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[4].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[5].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[6].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[7].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[8].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[9].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[10].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[11].income} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[12].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><td class="resPath">{xincome}</td></td>
                    <td class="resPath"></td>
                  </tr>

                  <tr>
                    <th scope="row" class="resPath">EXPENSES</th>
                    <td class="resPath"><input name="iBudget" type="number" className="inputSquare" value={this.state.mainarray[0].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iJan" type="number" className="inputSquare" value={this.state.mainarray[1].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iFeb" type="number" className="inputSquare" value={this.state.mainarray[2].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iMar" type="number" className="inputSquare" value={this.state.mainarray[3].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[4].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[5].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[6].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[7].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[8].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[9].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[10].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[11].expense} onChange={this.handleInputChange} /></td>
                    <td><input name="iApr" type="number" className="inputSquare" value={this.state.mainarray[12].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><td class="resPath">{xexpense}</td></td>
                    <td class="resPath"></td>
                  </tr>

                  <tr>
                    <th scope="row" class="resPath" >SAVINGS</th>
                    <td className="resPath">{this.state.mainarray[0].income - this.state.mainarray[0].expense}</td>
                    <td className="resSaving">{this.state.mainarray[1].income - this.state.mainarray[1].expense}</td>
                    <td className="resSaving">{this.state.mainarray[2].income - this.state.mainarray[2].expense}</td>
                    <td className="resSaving">{this.state.mainarray[3].income - this.state.mainarray[3].expense}</td>
                    <td className="resSaving">{this.state.mainarray[4].income - this.state.mainarray[4].expense}</td>
                    <td className="resSaving">{this.state.mainarray[5].income - this.state.mainarray[5].expense}</td>
                    <td className="resSaving">{this.state.mainarray[6].income - this.state.mainarray[6].expense}</td>
                    <td className="resSaving">{this.state.mainarray[7].income - this.state.mainarray[7].expense}</td>
                    <td className="resSaving">{this.state.mainarray[8].income - this.state.mainarray[8].expense}</td>
                    <td className="resSaving">{this.state.mainarray[9].income - this.state.mainarray[9].expense}</td>
                    <td className="resSaving">{this.state.mainarray[10].income - this.state.mainarray[10].expense}</td>
                    <td className="resSaving">{this.state.mainarray[11].income - this.state.mainarray[11].expense}</td>
                    <td className="resSaving">{this.state.mainarray[12].income - this.state.mainarray[12].expense}</td>
                    <td class="resPath"></td>
                    <td class="resPath"></td>
                  </tr>

                </tbody>
              </table>
            </form>

          </div>
          <div className="alert alert-primary">
            <p class="footer"><h3>2018</h3></p>
          </div>
        </div >
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark"></nav>
      </div>

    );
  }
}

export default App;
