import React, { Component } from "react";
import update from 'react-addons-update'; // ES6
import API from "./utils/API";
import "./App.css";

// 1. CREAR UN TABLA DE PRESUPUESTO ANUAL (BUDGET + 12 MESES CON INCOME, EXPENSES, RESULT)
