// FORMATO CON OTRAS CARACTERISTICAS
// GRAFICAR FORMATO


// AQUI EL COMPONENTE PRINCIPAL



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {

      mainarray: [
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
