          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        }
      ]

    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  componentDidMount() {
    this.loadBudget();
  }

  loadBudget = () => {
    API.getBudget()
      .then(res => { this.setState({ mainarray: res.data }) })
      .catch(err => console.log(err));
  };

  // PARA INGRESAR LOS CAMBIOS DE CADA CAMPO
  handleInputChange = event => {
    const target = event.target;
    const value = parseFloat(target.value);
    const month = target.dataset.month;
    let newstate = {};
    if (target.name === "income") {
      newstate[month.toString()] = { income: { $set: value } };
    }
    else {
      newstate[month.toString()] = { expense: { $set: value } };
    }
    this.setState({
      mainarray: update(this.state.mainarray, newstate)
    })
  }

  // SUBIR TODO EL FORMARTO CON EL BOTON
  handleFormSubmit = event => {
    event.preventDefault();
    API.updateBudget()


      // aqui mando el objeto como esta en el dom para actualizar



      .then(res => this.loadBudget())       // con el resultado reresamos el json actualizado y lo carga
      .catch(err => console.log(err));
  };

  render() {

    // CALCULOS

    let xincome = 0;
    let x1 = 0;
    let x2 = 0;
    let xexpense = 0;
    let i;
    for (i = 1; i < this.state.mainarray.length; i++) {
      x1 += parseFloat(this.state.mainarray[i].income);
      xincome = (x1 / 12).toFixed(2);
      x2 += parseFloat(this.state.mainarray[i].expense);
      xexpense = (x2 / 12).toFixed(2);
    };

    // loop para encabezados
    let rows = [];
    for (let i = 0; i < 13; i++) {
      rows.push('<th scope="col" className="resPath">{this.state.mainarray[' + i + '].month.toUpperCase()}</th>');
    }



    return (
      <div>

        <br />
        <div className="container">
          <div className="alert alert-primary">
            <h2>PERSONAL FINANCE</h2>
          </div>
          <div>
            <form>
              <table className="table table-sm">
                <thead>
                  <tr>
                    <th scope="col"><input type="submit" value="Submit All" className="btn btn-success" onClick={this.handleFormSubmit} /></th>
                  {rows}

                    {/* <th scope="col" className="resPath">{this.state.mainarray[0].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[1].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[2].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[3].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[4].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[5].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[6].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[7].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[8].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[9].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[10].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[11].month.toUpperCase()}</th>
                    <th scope="col" className="resPath">{this.state.mainarray[12].month.toUpperCase()}</th> */}
                    <th className="resPath" >AVERAGE</th>
                  </tr >
                </thead>

                <tbody>
                  <tr>
                    <th className="resPath" scope="row" >INCOME</th>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="0" value={this.state.mainarray[0].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="1" value={this.state.mainarray[1].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="2" value={this.state.mainarray[2].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="3" value={this.state.mainarray[3].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="4" value={this.state.mainarray[4].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="5" value={this.state.mainarray[5].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="6" value={this.state.mainarray[6].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="7" value={this.state.mainarray[7].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="8" value={this.state.mainarray[8].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="9" value={this.state.mainarray[9].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="10" value={this.state.mainarray[10].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="11" value={this.state.mainarray[11].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="income" data-month="12" value={this.state.mainarray[12].income} onChange={this.handleInputChange} /></td>
                    <td className="resPath">{xincome}</td>
                  </tr>

                  <tr>
                    <th scope="row" className="resPath">EXPENSES</th>

                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="0" value={this.state.mainarray[0].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="1" value={this.state.mainarray[1].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="2" value={this.state.mainarray[2].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="3" value={this.state.mainarray[3].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="4" value={this.state.mainarray[4].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="5" value={this.state.mainarray[5].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="6" value={this.state.mainarray[6].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="7" value={this.state.mainarray[7].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="8" value={this.state.mainarray[8].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="9" value={this.state.mainarray[9].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="10" value={this.state.mainarray[10].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="11" value={this.state.mainarray[11].expense} onChange={this.handleInputChange} /></td>
                    <td className="resPath"><input type="number" className="inputSquare" name="expense" data-month="12" value={this.state.mainarray[12].expense} onChange={this.handleInputChange} /></td>

                    <td className="resPath">{xexpense}</td>
                    <td className="resPath"></td>
