import React, { Component } from "react";
import update from 'react-addons-update'; // ES6
import API from "./utils/API";
import "./App.css";



// 1. CREAR UN TABLA DE PRESUPUESTO ANUAL (BUDGET + 12 MESES CON INCOME, EXPENSES, RESULT)
// 2. CAMPOS MODIFICABLES
// 3. Campos caluculados
// 4. Se trae de la base de datos, como datos precargados

// 4. SE GUARDE EN UNA BASE DE DATOS LOS CAMBIOS MONGODB
// 5. SE MUESTRE EL ARRAY GRABADO DE LA BASE DE DATOS

// MEJORAS
// LOOP PARA NO ESCRIBIR CELDA A CELDA

// DESEABLES
// PLATILLA POR USUARIO
// PLANTILLA POR AÃ‘O
// PLANTILLA EN LA QUE SE PUEDAN AGREGAR MAS FILAS PARA CALCULOS DETALLADOS


// AQUI EL COMPONENTE PRINCIPAL

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {

      mainarray: [
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        }, {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
          income: "0",
          expense: "0"
        }
      ]

    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  componentDidMount() {
    this.loadBudget();
  }

  loadBudget = () => {
    API.getBudget()
      .then(res => {
        this.setState({ mainarray: res.data })
      })
      .catch(err => console.log(err));
  };


  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const month = target.dataset.month;
    let newstate = {};
    if (target.name === "income") {
      newstate[month.toString()] = { income: { $set: value } };
    }
    else {
      newstate[month.toString()] = { expense: { $set: value } };
    }
    this.setState({
      mainarray: update(this.state.mainarray, newstate)
    })
  }


  handleFormSubmit = event => {
    event.preventDefault();
    API.saveBudget({
      mainarray

    })

      .then(res => this.loadBudget())
      .catch(err => console.log(err));

  };



  render() {

    // CALCULOS

    let xincome = 0;
    let x1 = 0;
    let x2 = 0;
    let xexpense = 0;
    let i;
    for (i = 1; i < this.state.mainarray.length; i++) {
      x1 += parseFloat(this.state.mainarray[i].income);
      xincome = (x1 / 12).toFixed(2);
      x2 += parseFloat(this.state.mainarray[i].expense);
      xexpense = (x2 / 12).toFixed(2);
    };

    //CREACION DE CELDAS INDEXADAS UNA TRAS OTRA

    // const numbers = [0, 1, 2, 3, 4, 5, 6];
    // const listItems = numbers.map((number) =>
    //   <th> {number}</th>

    // );


    return (
      <div>

        <br />
        <div className="container">
          <div className="alert alert-primary">
            <h2>PERSONAL FINANCE</h2>
          </div>
          <div>
            <form>
              <table className="table table-sm">
                <tr>
                  <th scope="col"><input type="submit" value="Submit All" class="btn btn-success" onClick={this.handleFormSubmit} /></th>

                  <th scope="col" className="resPath">{this.state.mainarray[0].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[1].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[2].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[3].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[4].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[5].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[6].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[7].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[8].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[9].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[10].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[11].month.toUpperCase()}</th>
                  <th scope="col" className="resPath">{this.state.mainarray[12].month.toUpperCase()}</th>

                  <th className="resPath" >AVERAGE</th>
                </tr >

                <tbody>
                  <tr>
                    <th scope="row" class="resPath" >INCOME</th>

                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="0" value={this.state.mainarray[0].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="1" value={this.state.mainarray[1].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="2" value={this.state.mainarray[2].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="3" value={this.state.mainarray[3].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="4" value={this.state.mainarray[4].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="5" value={this.state.mainarray[5].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="6" value={this.state.mainarray[6].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="7" value={this.state.mainarray[7].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="8" value={this.state.mainarray[8].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="9" value={this.state.mainarray[9].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="10" value={this.state.mainarray[10].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="11" value={this.state.mainarray[11].income} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="income" data-month="12" value={this.state.mainarray[12].income} onChange={this.handleInputChange} /></td>

                    <td class="resPath">{xincome}</td>

                  </tr>

                  <tr>
                    <th scope="row" class="resPath">EXPENSES</th>

                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="0" value={this.state.mainarray[0].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="1" value={this.state.mainarray[1].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="2" value={this.state.mainarray[2].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="3" value={this.state.mainarray[3].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="4" value={this.state.mainarray[4].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="5" value={this.state.mainarray[5].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="6" value={this.state.mainarray[6].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="7" value={this.state.mainarray[7].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="8" value={this.state.mainarray[8].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="9" value={this.state.mainarray[9].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="10" value={this.state.mainarray[10].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="11" value={this.state.mainarray[11].expense} onChange={this.handleInputChange} /></td>
                    <td class="resPath"><input type="number" className="inputSquare" name="expense" data-month="12" value={this.state.mainarray[12].expense} onChange={this.handleInputChange} /></td>

                    <td class="resPath">{xexpense}</td>
                    <td class="resPath"></td>
                  </tr>

                  <tr>
                    <th scope="row" class="resPath" >SAVINGS</th>
                    <td className="resPath">{this.state.mainarray[0].income - this.state.mainarray[0].expense}</td>
                    <td className="resSaving">{this.state.mainarray[1].income - this.state.mainarray[1].expense}</td>
                    <td className="resSaving">{this.state.mainarray[2].income - this.state.mainarray[2].expense}</td>
                    <td className="resSaving">{this.state.mainarray[3].income - this.state.mainarray[3].expense}</td>
                    <td className="resSaving">{this.state.mainarray[4].income - this.state.mainarray[4].expense}</td>
                    <td className="resSaving">{this.state.mainarray[5].income - this.state.mainarray[5].expense}</td>
                    <td className="resSaving">{this.state.mainarray[6].income - this.state.mainarray[6].expense}</td>
                    <td className="resSaving">{this.state.mainarray[7].income - this.state.mainarray[7].expense}</td>
                    <td className="resSaving">{this.state.mainarray[8].income - this.state.mainarray[8].expense}</td>
                    <td className="resSaving">{this.state.mainarray[9].income - this.state.mainarray[9].expense}</td>
                    <td className="resSaving">{this.state.mainarray[10].income - this.state.mainarray[10].expense}</td>
                    <td className="resSaving">{this.state.mainarray[11].income - this.state.mainarray[11].expense}</td>
                    <td className="resSaving">{this.state.mainarray[12].income - this.state.mainarray[12].expense}</td>
                    <td class="resPath"></td>
                    <td class="resPath"></td>
                  </tr>

                </tbody>
              </table>
            </form>

          </div>
          <div className="alert alert-primary">
            <p class="footer"><h3>2018</h3></p>
          </div>
        </div >
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark"></nav>
      </div>

    );
  }
}

export default App;
