// FORMATO CON OTRAS CARACTERISTICAS
// GRAFICAR FORMATO

// AQUI EL COMPONENTE PRINCIPAL

import React, { Component } from "react";
import update from 'react-addons-update'; // ES6
import API from "./utils/API";
import "./App.css";
import Nav from "./components/Nav";

// import Linear from "./components/Linear";

// import ReactDOM from 'react-dom';
// import { VictoryBar, VictoryChart, VictoryAxis, VictoryTheme, VictoryStack } from 'victory';


import { VictoryChart, VictoryVoronoiContainer, VictoryLine,VictoryTooltip } from 'victory';


class Linear extends React.Component {
  render() {
    return (
      <VictoryChart height={200} width={200}
        domainPadding={{ y: 20 }}
        containerComponent={
          <VictoryVoronoiContainer
            voronoiDimension="x"
            labels={(d) => `y: ${d.y}`}

          />}
      >
        <VictoryLine
          data={[
            { x: "JAN", y: 5},
            { x: 1.5, y: 5},
            { x: 2, y: 4},
            { x: 3, y: 0}
          ]}
          style={{
            data: {
              stroke: "tomato",
              strokeWidth: (d, active) => {return active ? 2 : 1;}
            },
            labels: { fill: "tomato" }
          }}
        />

        <VictoryLine
          data={[
            { x: 1, y: 0, l: "red" },
            { x: 2, y: 5, l: "green" },
            { x: 3, y: 3, l: "blue" }
          ]}
          style={{
            data: {
              stroke: "blue",
              strokeWidth: (d, active) => {return active ? 2 : 1;}
            },
            labels: { fill: "blue" }
          }}
        />

        <VictoryLine
          data={[
            { x: 1, y: 5, l: "cat" },
            { x: 2, y: 0, l: "dog" },
            { x: 3, y: 0, l: "bird" }
          ]}
          style={{
            data: {
              stroke: "black",
              strokeWidth: (d, active) => {return active ? 2 : 1;}
            },
            labels: { fill: "black" }
          }}
        />
      </VictoryChart>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {

      mainarray: [
        {
          month: "",
          income: "0",
          expense: "0"
        },
        {
          month: "",
