import axios from "axios";

export default {
  // Gets all budget

  getBudget: function () {
    return axios.get("/api/budgets");
  },
  // Saves changes in db
  saveBudget: function () {
    return axios.put("/api/budgets");
  },

  // Gets the budget with the given id **  NO LOS ESTOY USANDO **
  getBudgets: function (id) {
    return axios.get("/api/budgets/" + id);
  },
  // Deletes the book with the given id **  NO LOS ESTOY USANDO **
  deleteBudget: function (id) {
    return axios.delete("/api/budgets/" + id);
  }

};
