// AQUI EL COMPONENTE PRINCIPAL

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mainarray: []
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  componentDidMount() {
    this.loadBudget();
  }

  loadBudget = () => {
    API.getBudget()
      .then(res => this.setState({ mainarray: res.data}))
      .catch(err => console.log(err));
  };



  handleInputChange(event) {

    // POSICION A CAMBIAR: mainarray[0].income = 2000
    // <input type="number" className="inputSquare" name="income" data-month="0" value={this.state.mainarray[0].income} onChange={this.handleInputChange} />

    const target = event.target;
    const value = target.value;
    const name = target.name;


    const month = target.dataset.month;

    let newstate = {};
    newstate[month.toString()] = { income: { $set: value } };

    this.setState({
      mainarray: update(this.state.mainarray, newstate)
    })

  }



  render() {

    // CALCULOS

    let xincome = 0;
    let x1 = 0;
    let x2 = 0;
    let xexpense = 0;
    let i;
    for (i = 1; i < this.state.mainarray.length; i++) {
      x1 += parseFloat(this.state.mainarray[i].income);
      xincome = (x1 / 12).toFixed(2);
      x2 += parseFloat(this.state.mainarray[i].expense);
      xexpense = (x2 / 12).toFixed(2);
    };


